mapper.select:
    	SELECT COUPUNG.*, IFNULL(ORDER_DETAILS.SALES_RATE, 0) AS SALES_RATE 
    	FROM COUPUNG LEFT JOIN (SELECT COUPUNG_NUMBER, SUM(QUANTITY) AS SALES_RATE FROM ORDER_DETAILS GROUP BY COUPUNG_NUMBER) AS ORDER_DETAILS 
    	ON COUPUNG.COUPUNG_NUMBER = ORDER_DETAILS.COUPUNG_NUMBER

    		WHERE NAME LIKE CONCAT('%', #{searchValue}, '%') 


    		WHERE COUPUNG_CATEGORY_NUMBER = #{searchValue} 


    		WHERE AVAILABLE_CHECK = #{searchValue} 


			ORDER BY NAME 


			ORDER BY PRICE


			ORDER BY SALES_RATE DESC

    	LIMIT 20 OFFSET #{offset}

SELECT COUNT(*) FROM COUPUNG

	WHERE NAME LIKE CONCAT('%', #{searchValue}, '%') 


	WHERE COUPUNG_CATEGORY_NUMBER = #{searchValue} 


	WHERE AVAILABLE_CHECK = #{searchValue}

mapper.update:
UPDATE COUPUNG
SET NAME = #{name}
	, PRICE = #{price}
	, AVAILABLE_STOCK = #{availableStock}
WHERE COUPUNG_NUMBER = #{coupungNumber}

UPDATE COUPUNG
SET AVAILABLE_CHECK = #{availableCheck}
WHERE COUPUNG_NUMBER = #{coupungNumber}

mapper.select:
SELECT
COUNT(*) AS review_count
FROM REVIEWS
WHERE ORDER_DETAIL_NUMBER IN (
SELECT ORDER_DETAIL_NUMBER
FROM ORDER_DETAILS
WHERE COUPUNG_NUMBER = #{coupungNumber}
)
AND SCORE = #{score};

SELECT AVAILABLE_STOCK
FROM COUPUNG
WHERE COUPUNG_NUMBER = #{coupungNumber}

mapper.update:
UPDATE COUPUNG 
SET AVAILABLE_STOCK = #{availableStock}
	, AVAILABLE_CHECK = #{availableCheck} 
WHERE COUPUNG_NUMBER = #{coupungNumber}
