mapper.update:
UPDATE CARTS
SET QUANTITY = QUANTITY + 1 WHERE
CART_NUMBER = #{CART_NUMBER}

UPDATE CARTS
SET QUANTITY = #{QUANTITY}
WHERE CLIENT_ID = #{CLIENT_ID}
AND COUPUNG_NUMBER = #{COUPUNG_NUMBER}

mapper.delete:
DELETE CARTS FROM CARTS
WHERE CLIENT_ID = #{CLIENT_ID}
AND COUPUNG_NUMBER =
#{COUPUNG_NUMBER}
