mapper.select:
SELECT *
FROM CARTS
WHERE CLIENT_ID = #{clientId}
AND COUPUNG_NUMBER = #{coupungNumber}

mapper.update:
UPDATE CARTS
SET QUANTITY = #{quantity}
WHERE CLIENT_ID = #{clientId}
AND COUPUNG_NUMBER = #{coupungNumber}

mapper.delete:
DELETE CARTS FROM CARTS
WHERE CLIENT_ID = #{clientId}
AND COUPUNG_NUMBER =
#{coupungNumber}
